data = []

def retrieve(data, map, start_date, end_date):
    count = 0
    sentiment = 0
    top_score = 0
    top_comment = "No Comments Found"

    for i in map:
        if i[1] >= start_date and i[1] <= end_date:
            count += 1
            sentiment += int(data[i[0]][3])
            if int(data[i[0]][3]) > top_score:
                top_score = int(data[i[0]][3])
                top_comment = data[i[0]][2]

    return [count, top_comment, top_score, sentiment]

def query(search_term=None, start_date=None, end_date=None):
    if search_term == '':
        return [[]]

    conn = sqlite3.connect('reddit_data.db')
    cursor = conn.cursor()
    
    query = "SELECT * FROM reddit_data;"

    cursor.execute(query)

    data = cursor.fetchall()

    conn.close()

    if search_term == "Query 1":
        retrieved = retrieve(data, Tesla_Index, start_date, end_date)
        search_term = "Tesla"
    elif search_term == "Query 2":
        retrieved = retrieve(data, Battery_Index, start_date, end_date)
        search_term = "Battery"
    elif search_term == "Query 3":
        retrieved = retrieve(data, Elon_Musk_Index, start_date, end_date)
        search_term = "Elon Musk"
    elif search_term == "Query 4":
        retrieved = retrieve(data, Charging_Index, start_date, end_date)
        search_term = "Charging"
    elif search_term == "Query 5":
        retrieved = retrieve(data, Trump_Index, start_date, end_date)
        search_term = "Trump"

    data = [["Query", "Time Taken (ms)", "Number Of Results Found", "Top Comment", "Comment Score", "Net Sentiment"], [search_term, 0, retrieved[0], retrieved[1], retrieved[2], 0]]

    if retrieved[3] == 0:
        data[1][5] = "Neutral"
    elif retrieved[3] > 0:
        data[1][5] = "Positive"
    elif retrieved[3] < 0:
        data[1][5] = "Negative"
    
    return data

